import { REST, Routes, SlashCommandBuilder } from "discord.js";
import "dotenv/config";

const commands = [

  new SlashCommandBuilder()
    .setName("clockin")
    .setDescription("Clock in"),

  new SlashCommandBuilder()
    .setName("clockout")
    .setDescription("Clock out"),

  new SlashCommandBuilder()
    .setName("timesheet")
    .setDescription("Timesheet commands")

    .addSubcommand(sc =>
      sc
        .setName("view")
        .setDescription("View hours")
        .addUserOption(o =>
          o.setName("user").setDescription("User").setRequired(false)
        )
        .addStringOption(o =>
          o.setName("start").setDescription("MM/DD/YYYY")
        )
        .addStringOption(o =>
          o.setName("end").setDescription("MM/DD/YYYY")
        )
    )

    .addSubcommand(sc =>
      sc
        .setName("reset")
        .setDescription("Reset ALL users (Manager only)")
        .addStringOption(o =>
          o.setName("start").setDescription("MM/DD/YYYY")
        )
        .addStringOption(o =>
          o.setName("end").setDescription("MM/DD/YYYY")
        )
    ),

  new SlashCommandBuilder()
    .setName("totalhr")
    .setDescription("Show total hours for all users"),

].map(c => c.toJSON());

const rest = new REST({ version: "10" })
  .setToken(process.env.DISCORD_BOT_TOKEN);

await rest.put(
  Routes.applicationGuildCommands(
    process.env.CLIENT_ID,
    process.env.GUILD_ID
  ),
  { body: commands }
);

console.log("âœ… Commands deployed");
