import { REST, Routes, SlashCommandBuilder } from "discord.js";
import "dotenv/config";

const commands = [

  new SlashCommandBuilder()
    .setName("clockin")
    .setDescription("Clock in"),

  new SlashCommandBuilder()
    .setName("clockout")
    .setDescription("Clock out"),

  new SlashCommandBuilder()
    .setName("timesheet")
    .setDescription("Timesheet commands")

    // ===== VIEW =====
    .addSubcommand(sc =>
      sc
        .setName("view")
        .setDescription("View total hours")
        .addStringOption(o =>
          o.setName("start")
            .setDescription("MM/DD/YYYY")
        )
        .addStringOption(o =>
          o.setName("end")
            .setDescription("MM/DD/YYYY")
        )
    )

    // ===== RESET =====
    .addSubcommand(sc =>
      sc
        .setName("reset")
        .setDescription("Reset timesheet (Manager only)")
        .addStringOption(o =>
          o.setName("start")
            .setDescription("MM/DD/YYYY")
        )
        .addStringOption(o =>
          o.setName("end")
            .setDescription("MM/DD/YYYY")
        )
    )

].map(c => c.toJSON());

const rest = new REST({ version: "10" })
  .setToken(process.env.DISCORD_BOT_TOKEN);

console.log("ðŸš€ Deploying slash commands...");

await rest.put(
  Routes.applicationGuildCommands(
    process.env.CLIENT_ID,
    process.env.GUILD_ID
  ),
  { body: commands }
);

console.log("âœ… Slash commands deployed successfully");
